#!/bin/bash

Exec=bash -c '' 'dummy-param' %F

Exec=bash -c 'nameext=$(basename "$1"); nameext2=$(getdir=$(kdialog --title "Enter path" --getexistingdirectory ~/); echo "$getdir"; "$getdir"); dir=$(dirname "$1"); cd "$nameext2"; tar -t -f "$dir"/"$nameext" > ".""$nameext""_files"; rowcount=$(cat ".""$nameext""_files" | wc -l); i="1"; hits="0"; while ( [ $i -lt $((rowcount+1)) ] && [ $hits -lt 1 ] ); do content=$(sed -n "$i{p;q}" ".""$nameext""_files"); cont=${content%*/*}; if ( [ -d "$cont" ] || [ -f "$cont" ] ); then echo "file/folder exists"; hits=$((hits+1)); fi; i=$((i+1)); done; rm ".""$nameext""_files"; overw="0"; if [ $hits -gt 0 ]; then echo "file/folder exists"; overw=$(kdialog --warningyesno "Existing data must be overwritten to extract this archive. Do you want to overwrite them?"; case $? in 0) ow="0";; 1) ow="1";; esac; echo "$ow"; "$ow"); fi; echo $overw; cd "$dir"; if [ "$overw" == "0" ]; then tar -xf "$nameext" -C "$nameext2" --overwrite; kdialog --title "Extract" --msgbox "Extraction done!"; else kdialog --title "Extract" --error "Extraction aborted!"; fi;' 'dummy-param' %F